import React, { useEffect, useRef, memo } from "react";
import { useSelector, useDispatch, shallowEqual } from "react-redux";
import classnames from "classnames";

import {
  getRadioCategories,
  changeCurrentIdAction,
} from "../../store/actionCreators";

import { Carousel } from "antd";
import { CategoryWrapper, CategoryContent, CategoryItemImage } from "./style";

export default memo(function LJRadioCategory() {
  // redux hooks
  const dispatch = useDispatch();
  const { categories = [], currentId = 0 } = useSelector(
    (state) => ({
      categories: state.getIn(["djRadio", "categories"]),
      currentId: state.getIn(["djRadio", "currentId"]),
    }),
    shallowEqual
  );

  // react hooks
  useEffect(() => {
    dispatch(getRadioCategories());
  }, [dispatch]);
  const carouselRef = useRef();

  // handle function

  // 计算出共有几页，每页应当有19个类目
  // ceil 向下取整
  // const page = Math.ceil(categories.length / PAGE_SIZE) || 1;
  const PAGE_SIZE = 18;
  const page = 2;

  // 网易云中一共有20项，但这里获取的只有18项，最后两项先随便填充一下

  function getSize(index) {
    return index === 0 ? 16 : 18;
  }

  return (
    <CategoryWrapper>
      <div
        className="arrow arrow-left"
        onClick={(e) => carouselRef.current.prev()}
      ></div>
      <CategoryContent>
        <Carousel dots={{ className: "dots" }} ref={carouselRef}>
          {Array(page)
            .fill(0)
            .map((_, index) => {
              return (
                <div key={index} className="category-page">
                  {/* categories: 0-17 , 18-end */}
                  {/* slice:0,17 , 17,19 */}
                  {/* slice:0,16 , 16,18 */}
                  {categories
                    .slice(index * PAGE_SIZE, getSize(index + 1))
                    .map((item, indey) => {
                      return (
                        <div
                          key={item.id}
                          onClick={(e) =>
                            dispatch(changeCurrentIdAction(item.id))
                          }
                          className={classnames("category-item", {
                            active: currentId === item.id,
                          })}
                        >
                          <CategoryItemImage
                            className="image"
                            imgUrl={item.picWebUrl}
                          ></CategoryItemImage>
                          <span>{item.name}</span>
                        </div>
                      );
                    })}
                </div>
              );
            })}
        </Carousel>
      </CategoryContent>
      <div
        className="arrow arrow-right"
        onClick={(e) => carouselRef.current.next()}
      ></div>
    </CategoryWrapper>
  );
});
