import request from "./request";

export function getSongCategory() {
  return request({
    url: "/playlist/catlist",
  });
}

export function getSongCategoryList(cat = "流行", offset = 0, limit = 35) {
  return request({
    url: "/top/playlist",
    params: {
      cat,
      limit,
      offset,
    },
  });
}
