import request from "./request";

export function getTopBanners() {
  return request({
    url: "/banner",
  });
}

export function getHotRecommends(limit) {
  return requestIdleCallback({
    // url: "/personalized?limit=8",
    url: "/personalized",
    params: {
      limit,
    },
  });
}
