import styled from "styled-components";

import mine_sprite from "@/assets/img/mine_sprite.png";

export const MineWrapper = styled.div`
  .main {
    background-color: #fff;
    /* min-height: 700px; */

    .image {
      position: relative;
      width: 807px;
      height: 372px;
      margin: 0 auto;
      background: url(${mine_sprite}) 0 104px no-repeat;
    }
  }
`;
