import styled from "styled-components";
import progress_bar from "@/assets/img/progress_bar.png";
import sprite_icon from "@/assets/img/sprite_icon.png";

export const PlayerVolumeWrapper = styled.div`
  position: absolute;
  left: 88%;
  bottom: 46px;
  background-position: 0 -503px;

  .ant-slider {
    display: "inline-block";
    height: 100px;

    .ant-slider-rail {
      height: 9px;
      background: url(${progress_bar}) right 0;
    }

    .ant-slider-track {
      background: url(${progress_bar}) -40px bottom;
    }

    .ant-slider-handle {
      width: 22px;
      height: 24px;
      border: none;
      margin-top: -7px;
      background: url(${sprite_icon}) 0 -250px;
    }
  }
`;
