import React, { memo, Fragment } from "react";
import classnames from "classnames";

import { getSizeImage } from "@/utils/format-utils";

import { ArtistItemWrapper } from "./style";

export default memo(function HYArtistItemV1(props) {
  const { info, index } = props;
  console.log(index);

  return (
    <ArtistItemWrapper>
      {/* 前10个有照片，后面只有名字 */}
      {index < 10 && (
        <Fragment className="image">
          <img src={getSizeImage(info.img1v1Url, 130)} alt={info.name} />
          <div className="info">
            <span className="name">{info.name}</span>
            <i className="sprite_icon2 icon"></i>
          </div>
        </Fragment>
      )}
      {index > 9 && (
        <Fragment>
          <div></div>
          <div className="info nonePic">
            <span className="name">{info.name}</span>
            <i className="sprite_icon2 icon"></i>
          </div>
        </Fragment>
      )}
    </ArtistItemWrapper>
  );
});
