import styled from "styled-components";

import friend_sprite from "@/assets/img/friend_sprite.png";
export const FriendWrapper = styled.div`
  .content {
    background-color: #fff;
    min-height: 700px;

    .pic {
      position: relative;
      width: 807px;
      height: 484px;
      margin: 0 auto;
      background: url(${require("@/assets/img/friend_sprite.jpg")}) 0 104px
        no-repeat;

      .login {
        position: absolute;
        width: 167px;
        height: 45px;
        left: 482px;
        top: 368px;
      }
      .login:hover {
        background: url(${mine_sprite}) 0 -274px no-repeat;
      }
    }
  }
`;
