import React, { useState, memo } from "react";
import { useSelector, useDispatch, shallowEqual } from "react-redux";

import { PER_PAGE_NUMBER } from "@/common/contants";
import { getSongListAction } from "../../store/actionCreators";

import LJSongsCover from "@/components/songs-cover";
import LJPagination from "@/components/songs-pagination";
import { SongsMainWrapper } from "./style";
export default memo(function LJSongsMain() {
  // props
  const [currentPage, setCurrentPage] = useState(1);

  // redux hoooks
  const { categorySongs } = useSelector(
    (state) => ({
      categorySongs: state.getIn(["songs", "categorySongs"]),
    }),
    shallowEqual
  );
  const songList = categorySongs.playlists || [];
  const total = categorySongs.total || 0;
  const dispatch = useDispatch();

  // handle function
  function onPageChange(page, pageSize) {
    setCurrentPage(page);
    dispatch(getSongList(page));
  }

  return (
    <SongsMainWrapper>
      <div className="songs-list">
        {songList.map((item, index) => {
          return (
            <HYThemeCover info={item} key={item.id + index} right="30px" />
          );
        })}
      </div>
      <HYPagination
        currentPage={currentPage}
        total={total}
        pageSize={PER_PAGE_NUMBER}
        onPageChange={onPageChange}
      />
    </SongsMainWrapper>
  );
});
