import React, { memo, Suspense, useState } from "react";
import { Provider } from "react-redux";
import { renderRoutes } from "react-router-config";
import { HashRouter } from "react-router-dom";

// 第三方导入
import routes from "./router";
import store from "./store";

// 组件导入
import LJAppHeader from "@/components/app-header";
import LJAppFooter from "@/components/app-footer";
import LJAppPlayBar from "@/pages/player/app-player-bar";

export default memo(function App() {
  // 显示/隐藏底部的bar
  //     visibility: hidden;
  const [showVolum, setshowVolumn] = useState(false);
  const [showPannel, setshowPannel] = useState(false);

  // handle func
  const handleShow = () => {
    setshowVolumn(false);
    setshowPannel(false);
  };

  return (
    // 共享 store
    <Provider store={store}>
      <HashRouter>
        <div onClick={(e) => handleShow()}>
          <LJAppHeader />
          <Suspense fallback={<h2>Page Loading...</h2>}>
            {renderRoutes(routes)}
          </Suspense>
          <LJAppFooter />
        </div>
        <LJAppPlayBar />
      </HashRouter>
    </Provider>
  );
});
