import * as actionTypes from "./constants";

import { getSongCategoryList, getSongCategory } from "@/services/solgList";
// 不暴露，内部action
const changeCategoryAction = (res) => ({
  type: actionTypes.CHANGE_CATEGORY,
  category: res,
});

const changeCategoryListAction = (res) => ({
  type: actionTypes.CHANGE_SONG_LIST,
  songList: res,
});

// 暴露，异步action
export const getSongCategoryAction = (index) => {
  return (dispatch) => {
    getSongCategory(index).then((res) => {
      dispatch(changeCategoryAction());
    });
  };
};

export const getSongCategoryListAction = (cat, offset, limit) => {
  return (dispatch) => {
    getSongCategoryList(cat, offset, limit).then((res) => {
      dispatch(changeCategoryListAction());
    });
  };
};
