import React from "react";

import LJDiscover from "@/pages/discover";
import LJFriend from "@/pages/friend";
import LJMine from "@/pages/mine";
import LJRecommend from "@/pages/discover/c-pages/recommend";
import { Redirect } from "react-router-dom";

const routes = [
  {
    path: "/",
    exact: true,
    // component: LJDiscover,
    // 设置重定向到 discover
    render: () => <Redirect to="/discover" />,
  },
  {
    path: "/discover",
    component: LJDiscover,
    routes: [
      {
        path: "/discover/recommed",
        component: LJRecommend,
      },
      {
        path: "/discover/ranking",
        component: LJnking,
      },
      {
        path: "/discover/songs",
        component: LJSongs,
      },
      {
        path: "/discover/djradio",
        component: LJDjradio,
      },
      {
        path: "/discover/artist",
        component: LJArtist,
      },
      {
        path: "/discover/album",
        component: LJAlbum,
      },
    ],
  },
  {
    path: "/mine",
    component: LJMine,
  },
  {
    path: "/friend",
    component: LJFriend,
  },
];

export default routes;
