import styled from "styled-components";

export const SetterSongerWrapper = styled.div`
  padding: 20px 0;

  .singer-list {
    .item {
      display: flex;
      height: 62px;
      margin-top: 14px;
      background-color: #fafafa;
      text-decoration: none;

      :hover {
        background-color: #f4f4f4;
      }

      img {
        width: 62px;
        height: 62px;
      }

      .info {
        margin: 8px 0 0 10px;
        .title {
          color: #333;
          font-size: 14px;
          font-weight: 700;
        }

        .name {
          margin-top: 5px;
        }
      }
    }
  }

  .apply-for {
    a {
      margin-left: 20px;
      border-radius: 4px;
      color: #333;
      background-position: right -100px;
      text-decoration: none;
      padding: 0 5px 0 0;
      white-space: nowrap;
      display: inline-block;
      height: 31px;
      line-height: 31px;
      overflow: hidden;
      vertical-align: top;
      text-align: center;
      cursor: pointer;
      outline: none;

      /* margin-left: 20px;
      display: inline-block;
      width: 210px;
      height: 31px;
      line-height: 31px;
      background-position: right -100px;

      padding: 0 5px 0 0;
      white-space: nowrap;

      color: #333;
      font-weight: 700;
      text-align: center;
      border-radius: 4px;
      background-color: #fafafa;
      border: 1px solid #c3c3c3;
      text-decoration: none;
      cursor: pointer; */
    }

    i {
      display: inline-block;
      height: 31px;
      line-height: 31px;
      overflow: hidden;

      width: 170px;
      font-weight: bold;
      color: #333;
      background-position: 0 -59px;
      padding: 0 15px 0 20px;
      pointer-events: none;
    }
  }
`;
