import React, { memo, useCallback, useEffect, useState } from "react";
import { useDispatch, useSelector, shallowEqual } from "react-redux";
import { Slider } from "antd";

import { INIT_VOLUME } from "@/common/contants";
import { changePlayerVolumeAction } from "../../../store/actionCreators";

import { PlayerVolumeWrapper } from "./style";

export default memo(function LJPlayerVolume() {
  // 进度条
  const [progress, setProgress] = useState(0);
  // 初始化音量，获取数据
  const { volume } = useSelector(
    (state) => ({
      volume: state.getIn(["player", "volume"]),
    }),
    shallowEqual
  );

  const dispatch = useDispatch();

  // 对音量的初始化
  useEffect(() => {
    dispatch(changePlayerVolumeAction(INIT_VOLUME));
    setProgress(INIT_VOLUME / 100);
  }, []);

  // 按下
  const sliderChange = useCallback(
    (value) => {
      setProgress(value);
      dispatch(changePlayerVolumeAction(value / 100));
    },
    [dispatch]
  );

  // // 松开
  // const sliderAfterChange = useCallback(
  //   (value) => {
  //     const currentVolume = value / 100;
  //     dispatch(changePlayerVolumeAction(currentVolume));
  //   },
  //   [dispatch]
  // );

  return (
    <PlayerVolumeWrapper className="sprite_playbar">
      <Slider
        vertical
        className="sprite_playbar"
        value={progress}
        onChange={sliderChange}
        // onAfterChange={sliderAfterChange}
      />
    </PlayerVolumeWrapper>
  );
});
