import React, { memo } from "react";
import { useDispatch } from "react-redux";

import { getSizeImage } from "@/utils/format-utils";
import { getSongDetailAction } from "@/pages/player/store/index";

import { TopRankingWrapper } from "./style";

export default memo(function LJTopRanking(props) {
  // props and state
  const { info = [] } = props;
  // 如果第一次加载，info尚未下载完成，是 undefined 无法访问 info.tracks
  // 初始化为 []
  const { tracks = [] } = info;

  // redux hooks
  const dispatch = useDispatch();

  // react hooks

  // other handle
  const playMusic = (item) => {
    dispatch(getSongDetailAction(item.id));
  };

  return (
    <TopRankingWrapper>
      <div className="header">
        <div className="image">
          <img src={getSizeImage(info.coverImgUrl, 100)} alt="" />
          <a href="/todo" className="image_cover">
            ranking
          </a>
        </div>
        <div className="info">
          <a href="/todo">{info.name}</a>
          <div>
            <button className="btn play sprite_02">123</button>
            <button className="btn favor sprite_02">123</button>
          </div>
        </div>
      </div>
      <div className="list">
        {tracks.slice(0, 10).map((item, index) => {
          return (
            <div key={item.id} className="list-item">
              <div className="rank">{index + 1}</div>
              <div className="info">
                <span className="name text-nowrap">{item.name}</span>
                <div className="operate">
                  <button
                    className="btn play sprite_02"
                    onClick={(e) => playMusic(item)}
                  ></button>
                  <button className="btn addto sprite_icon2"></button>
                  <button className="btn favor sprite_02"></button>
                </div>
              </div>
            </div>
          );
        })}
      </div>
      <div className="footer">
        <a href="/todo">查看全部{">"}</a>
      </div>
    </TopRankingWrapper>
  );
});
