import React, { memo, useEffect } from "react";
import { useDispatch, useSelector, shallowEqual } from "react-redux";

// import {} from "@/common/contants";
import { getRankingListAction } from "../../store/actionCreators";

import LJThemeHeaderRCM from "@/components/theme-header-rcm";
import LJRankingList from "@/components/top-ranking";
import { RankingWrapper } from "./style";

export default memo(function LJRankingList() {
  // component state：

  // redux hooks：          get state dispatch
  const { rankingList } = useSelector(
    (state) => ({
      rankingList: state.getIn(["recommend", "rankingList"]),
    }),
    shallowEqual
  );
  const dispatch = useDispatch();

  // other hooks：          react hooks
  useEffect(() => {
    dispatch(getRankingListAction(0));
    dispatch(getRankingListAction(2));
    dispatch(getRankingListAction(3));
  }, [dispatch]);

  // other function：       组件内部的其他逻辑

  return (
    <RankingWrapper>
      <LJThemeHeaderRCM title="榜单" />
      <div className="tops">
        <LJRankingList></LJRankingList>
      </div>
      {/* <div>
        {rankingList.map((item, index) => {
          return (
            <div key={item.id}>
              <h2>{item.name}</h2>
              <h4>{item.id}</h4>
            </div>
          );
        })}
      </div> */}
    </RankingWrapper>
  );
});
